        <div className="messages">
          {messages.map((msg, idx) => (
            <div key={idx} className={msg.from}>
              <strong>{msg.from === 'user' ? 'üßç ÏÇ¨Ïö©Ïûê' : 'ü§ñ Ï±óÎ¥á'}:</strong> {msg.text}
            </div>
          ))}
        </div>
        <button onClick={goHome}>‚Üê ÎèåÏïÑÍ∞ÄÍ∏∞</button>
      </div>
    );
  }

  export default Chatbot;
