import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import Login from './components/Login';
import SearchBox from './components/SearchBox';
import './styles/App.css';

function App() {
  const [isLoginPage, setIsLoginPage] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [history, setHistory] = useState([]);

  const handleLogin = () => {
    setIsLoggedIn(true);
    setIsLoginPage(false);
  };

  const goToLogin = () => setIsLoginPage(true);
  const goHome = () => setIsLoginPage(false);

  const handleSearch = (query) => {
    setHistory([query, ...history]);
  };

  if (isLoginPage) {
    return <Login onLogin={handleLogin} goHome={goHome} />;
  }

  return (
    <div className="app-container">
      <div className="header">
        <div className="logo" onClick={goHome}>Fake News Checker</div>
        <div className="login-button-wrapper">
         {!isLoggedIn && <button onClick={goToLogin}>로그인</button>}
         </div>
      </div>
      <Sidebar history={history} />
      <SearchBox onSearch={handleSearch} />
    </div>
  );
}

export default App;
