import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import Login from './components/Login';
import SearchBox from './components/SearchBox';
import './styles/App.css';
import Signup from './components/Signup';
import Chatbot from './components/Chatbot';

function App() {
  const [isLoginPage, setIsLoginPage] = useState(false);
  const [isSignupPage, setIsSignupPage] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [history, setHistory] = useState([]);
  const [chatMode, setChatMode] = useState(false);
  const [chatQuery, setChatQuery] = useState('');

  const handleLogin = () => {
    setIsLoggedIn(true);
    setIsLoginPage(false);
  };

  const goToLogin = () => {
    setIsLoginPage(true);
    setIsSignupPage(false);
  };

  const goHome = () => {
    setIsLoginPage(false);
    setIsSignupPage(false);
  };

  const goToSignup = () => {
    setIsSignupPage(true);
    setIsLoginPage(false);
  };

  if (isLoginPage) {
    return <Login onLogin={handleLogin} goHome={goHome} goSignup={goToSignup} />;
  }

  if (isSignupPage) {
    return <Signup goHome={goHome} />;
  }

  const handleSearch = (query) => {
    setHistory([query, ...history]);
    setChatMode(false);      // 대화창으로 전환
  };
  const exitChatMode = () => setChatMode(false);

  if (chatMode) {
    return <Chatbot query={chatQuery} goHome={goHome} />;
  }


  return (
    <div className="app-container">
      <div className="header">
        <div className="logo" onClick={goHome}>Fake News Checker</div>
        <div className="login-button-wrapper">
          {!isLoggedIn && <button onClick={goToLogin}>로그인</button>}
        </div>
      </div>

      <div className="main-content">
        {chatMode ? (
          <Chatbot query={chatQuery} goHome={() => setChatMode(false)} />
        ) : (
          <>
            <Sidebar history={history} />
            <SearchBox onSearch={handleSearch} />
            <Chatbot query={chatQuery} goHome={exitChatMode} />
          </>
        )}
      </div>
    </div>
  );
}

export default App;
